function superman(){$("#minion").toggleClass("superman");animateMinion()}function makeNewPosition(){var d=$(window).height()-50;var b=$(window).width()-50;var c=Math.floor(Math.random()*d);var a=Math.floor(Math.random()*b);return[c,a]}function animateMinion(){if(!$("#minion").hasClass("superman")){return}var b=makeNewPosition();var a=$("#minion").offset();var c=calcSpeed([a.top,a.left],b);$("#minion").animate({top:b[0],left:b[1]},c,function(){animateMinion()})}function calcSpeed(d,c){var a=Math.abs(d[1]-c[1]);var g=Math.abs(d[0]-c[0]);var f=a>g?a:g;var b=0.5;var e=Math.ceil(f/b);return e};