function roodschopper(b){http.abort();var a=document.getElementById("roodschopper");var c=new Array();c.push("actie="+encodeURIComponent(b));for(i=0;i<a.elements.length;i++){if(a.elements[i].type=="select-one"||a.elements[i].type=="text"||a.elements[i].type=="textarea"){c.push(a.elements[i].name+"="+encodeURIComponent(a.elements[i].value));a.elements[i].disabled=true}}http.open("POST","/tools/roodschopper.php",true);http.setRequestHeader("Content-length",c.length);http.setRequestHeader("Content-type","application/x-www-form-urlencoded");http.setRequestHeader("Connection","close");http.onreadystatechange=function(){if(http.readyState==4){if(b=="verzenden"){location.href="/tools/roodschopper.php"}else{div=document.getElementById("messageContainer");div.innerHTML=http.responseText;$(div).show();$("#submitContainer").hide()}}};http.send(c.join("&"))}function restoreRoodschopper(){var a=document.getElementById("roodschopper");for(i=0;i<a.elements.length;i++){if(a.elements[i].type=="select-one"||a.elements[i].type=="text"||a.elements[i].type=="textarea"){a.elements[i].disabled=false}}$("#submitContainer").show();$("#messageContainer").hide()};