var kringen=new Array();function toggleEmails(a){if(typeof(kringen[a])=="undefined"){http.abort();http.open("GET","/communicatie/verticalen.php?email="+a,true);http.onreadystatechange=function(){if(http.readyState==4){kringen[a]=document.getElementById("leden"+a).innerHTML;document.getElementById("leden"+a).innerHTML=http.responseText;selectText("leden"+a)}};http.send(null)}else{document.getElementById("leden"+a).innerHTML=kringen[a];delete kringen[a]}};