CREATE TABLE groep_categorien (categorie_id int(11) NOT NULL AUTO_INCREMENT, titel varchar(255) NOT NULL, toon_overzicht boolean NOT NULL, toon_profiel boolean NOT NULL, sync_ldap boolean NOT NULL, rechten_aanmaken varchar(255) NOT NULL, rechten_beheren varchar(255) NOT NULL, rechten_bekijken varchar(255) NOT NULL, rechten_aanmelden varchar(255) NOT NULL, PRIMARY KEY (categorie_id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1;