CREATE TABLE groep_leden (groep_type varchar(255) NOT NULL, groep_id int(11) NOT NULL, lid_id varchar(4) NOT NULL, opmerking varchar(255) NOT NULL, lid_sinds datetime NOT NULL, lid_tot datetime DEFAULT NULL, status varchar(4) NOT NULL, prioriteit int(11) NOT NULL, door_lid_id varchar(4) NOT NULL, PRIMARY KEY (groep_type, groep_id, lid_id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1;